import "./App.css";
import { useState } from "react";
import {
  BookCurrentlyReading,
  BookWantToRead,
  BookRead,
} from "./components/Books/Book";
import { search } from "./BooksAPI";
import Book from "./components/Books/Book";

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);
  const [searchValue, setSearchValue] = useState("");
  const [searchResult, setSearchResult] = useState([]);

  const handleChange = async (event) => {
    event.preventDefault();
    setSearchValue(event.target.value);
    console.log(event.target.value);
    let result = await search(event.target.value);
    setSearchResult(result);
    console.log(result, "result");
  };

  return (
    <div className="app">
      {showSearchPage ? (
        <div className="search-books">
          <div className="search-books-bar">
            <a
              className="close-search"
              onClick={() => setShowSearchpage(!showSearchPage)}
            >
              Close
            </a>
            <div className="search-books-input-wrapper">
              <input
                type="text"
                placeholder="Search by title, author, or ISBN"
                name="searchValue"
                value={searchValue}
                onChange={handleChange}
              />
            </div>
          </div>
          <div className="search-books-results">
            <ol className="books-grid">
              {searchResult.map((result, id) => {
                return (
                  <div className="bookshelf-books">
                    <ol className="books-grid">
                      <li>
                        <div className="book">
                          <div className="book-top">
                            <div
                              id="to-kill-a-mockingbird"
                              className="book-cover"
                            >
                              {result.imageLinks.thumbnail}
                            </div>
                            <BookShelfChanger />
                          </div>
                          <div className="book-title">
                            <div className="bookshelf-books">
                              <ol className="books-grid">
                                <li>
                                  <div className="book">
                                    <div className="book-top">
                                      <div
                                        id="to-kill-a-mockingbird"
                                        className="book-cover"
                                      ></div>
                                      <BookShelfChanger />
                                    </div>
                                    <div className="book-title">
                                      {result.title}
                                    </div>
                                    <div className="book-authors">
                                      {result.authors.map((author) => author)}
                                    </div>
                                  </div>
                                </li>
                              </ol>
                            </div>
                          </div>
                          <div className="book-authors">Harper Lee</div>
                        </div>
                      </li>
                      <li>
                        <div className="book">
                          <div className="book-top">
                            <div id="enders-game" className="book-cover"></div>
                            <BookShelfChanger />
                          </div>
                          <div className="book-title">Ender's Game</div>
                          <div className="book-authors">Orson Scott Card</div>
                        </div>
                      </li>
                    </ol>
                  </div>
                  // <>
                  //   <p>{result.imageLinks.thumbnail}</p>
                  //   <p>{result.title}</p>
                  //   <p>{result.authors.map((author) => author)}</p>
                  // </>
                );
              })}
            </ol>
          </div>
        </div>
      ) : (
        <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
          </div>
          <div className="list-books-content">
            <div>
              <BookCurrentlyReading />
              <BookWantToRead />
              <BookRead />
            </div>
          </div>
          <div className="open-search">
            <a onClick={() => setShowSearchpage(!showSearchPage)}>Add a book</a>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
