import { useState } from "react";
import { getAll, update } from "../../BooksAPI";

const BookSearch = ({ setBookResult, book }) => {
  const [shelfValue, setShelfValue] = useState(book.shelf || "none");
  return (
    <div className="book">
      <div className="book-top">
        <div
          className="book-cover"
          style={{
            backgroundImage: `url(${book.imageLinks.thumbnail})`,
          }}
        ></div>
        <div className="book-shelf-changer">
          <select
            value={shelfValue}
            onChange={(e) => {
              setShelfValue(e.target.value);
              update(book, e.target.value).then((updatedShelveBook) => {
                getAll().then((books) => setBookResult(books));
              });
            }}
          >
            <option value="none" disabled>
              Add to...
            </option>
            <option value="currentlyReading">Currently Reading</option>
            <option value="wantToRead">Want to Read</option>
            <option value="read">Read</option>
          </select>
        </div>
      </div>
      <div className="book-title">{book.title}</div>
      <div className="book-authors">
        {book.authors ? book.authors.join(", ") : "unknown author"}
      </div>
    </div>
  );
};

export default BookSearch;
