import "./App.css";
import { useState } from "react";
import { get, search, update } from "./BooksAPI";
import "./components/Books/book.css";
import {
  BookCurrentlyReading,
  BookRead,
  BookWantToRead,
} from "./components/Books/Book";
import BookShelfChanger from "./components/BookShelfChanger";

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);
  const [searchValue, setSearchValue] = useState("");
  const [searchResult, setSearchResult] = useState([]);
  // const [chooseShelf, SetChooseShelf] = useState([]);

  const handleChange = async (event) => {
    event.preventDefault();
    setSearchValue(event.target.value);
    let result = await search(event.target.value);
    setSearchResult(result);
  };

  // const changeShelf = async (event, id) => {
  //   event.preventDefault();
  //   SetChooseShelf(event.target.value);
  //   const chooseBook = searchResult.map((search) => search.id === id);
  //   const updateShelf = await update(chooseBook, chooseShelf);
  //   console.log("update shelf", updateShelf);
  // };

  return (
    <div className="app">
      {showSearchPage ? (
        <div className="search-books">
          <div className="search-books-bar">
            <a
              className="close-search"
              onClick={() => setShowSearchpage(!showSearchPage)}
            >
              Close
            </a>
            <div className="search-books-input-wrapper">
              <input
                type="text"
                placeholder="Search by title, author, or ISBN"
                name="searchValue"
                value={searchValue}
                onChange={handleChange}
              />
            </div>
          </div>
          <div className="search-books-results">
            <ol className="books-grid">
              {searchResult.map((result, id) => {
                return (
                  <li>
                    <div className="book">
                      <div className="book-top">
                        <div className="book-cover">
                          <img
                            src={result.imageLinks.thumbnail}
                            alt={result.title}
                          />
                        </div>
                        <BookShelfChanger />
                      </div>
                      <div className="book-title">{result.title}</div>
                      <div className="book-authors">
                        {result.authors.map((author) => author)}
                      </div>
                    </div>
                  </li>
                );
              })}
            </ol>
          </div>
        </div>
      ) : (
        <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
          </div>
          <div className="list-books-content">
            <div>
              <BookCurrentlyReading />
              <BookWantToRead />
              <BookRead />
            </div>
          </div>
          <div className="open-search">
            <a onClick={() => setShowSearchpage(!showSearchPage)}>Add a book</a>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
